sys_script.script:
(function executeRule(current, previous /*null when async*/) {

        // definição de variaveis
        // var righnow = agora \ var whenneeded = Quando necessário.



	// Instantiate the NeedItUtils class.  Call the isDatePast method and pass 
  // the u_when_needed value.
  var niutils = new NeedItUtils();
  var isPast = niutils.isDatePast(current.u_when_needed);

  // If the isDatePast method returns true, the date is in the past.
  if(isPast == true){
    gs.addErrorMessage("Quando a data necessária não pode estar no passado. Sua solicitação não foi salva no banco de dados.");
    current.setAbortAction(true);
  }



    // Desafio: Não permitir requisições no mesmo dia.
  // Obtém a parte da data de rightnow e whenNeeded (sem marcação de horário).
  var today = rightnow.getLocalDate();
  var istoday = whenNeeded.getLocalDate();

  // Compare hoje é hoje para ver se são o mesmo dia
  if(today.compareTo(istoday) == 0){
    gs.addErrorMessage("Você não pode enviar solicitações NeedIt para hoje..");
    current.setAbortAction(true);
  }

 }

)(current, previous);
