sys_script_include.script:
function validateEmailAddress(emailStr){
   // Use JavaScript para garantir que emailStr seja uma string
    emailStr = emailStr + '';
    // Compara emailStr com a sintaxe permitida conforme especificado na expressão regular
    // Se emailStr permitiu a sintaxe, retorne true, senão retorne false
    if(emailStr.match(/^(([^<>()\[\]\\.,;:\s@"]+(\.[^<>()\[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/)){
      return true;
    }
    else {
     return false;
    }
}
